import React, { PureComponent } from 'react';
import './App.css';
import axios from 'axios';
import styles from './mystyle.module.css'; 

interface VideoState {
    currentVideo: any;
    currentfilename: any;
};

class VideoPage extends  PureComponent<{}, VideoState> {
    constructor(props: {}) {
        super(props);

        this.state = {
            currentVideo: {},
            currentfilename: '',
        };
    }

    componentDidMount = () => {
        this.setState({currentfilename: window.location.href.split("/video").pop()});
        this.fetchVideo(this.state.currentfilename);
    }

    fetchVideo = (filename: any) => {
        axios.get('http://localhost:9890/video/' + filename)
            .then((response) => {
                this.setState({ recentImage: response.data.image });
            })
            .catch(err => alert('Error: ' + err));
    }

    render() {
        return (
            <div className={styles.Recent}>
            <video src={'http://localhost:9890/image' + window.location.href.split("/video").pop()} className="videoplayer"  width = "800" height = "600" controls>
            </video>
                <div>
            <p>{this.state.currentVideo.caption}</p>
            </div>
        </div>
        );
    }
}

export default VideoPage;
